function convertTemp(){celsius="fahrenheit"==$("input[name=temperatureUnit]:checked").val()?(temperature-32)/1.8:temperature,$("#celsius").text(celsius),changeBackground()}function changeBackground(){0>celsius?($("body").css("background-color","#3300FF"),$("body").css("color","white")):11>celsius?($("body").css("background-color","#99CCFF"),$("body").css("color","white")):21>celsius?($("body").css("background-color","#FFFF00"),$("body").css("color","grey")):31>celsius?($("body").css("background-color","#FF9900"),$("body").css("color","black")):41>celsius?($("body").css("background-color","#FF6600"),$("body").css("color","black")):($("body").css("background-color","#FF0000"),$("body").css("color","black"))}var temperature=$("#temperature").val(),celsius=0,fahrenheit=0;$("#convertor").submit(function(e){e.preventDefault(),temperature=1*$("#temperature").val(),$.isNumeric($("#temperature").val())?convertTemp():alert("Please enter in a valid temperature value.")});